package com.destiny.alatmusiktradisionalindonesia.Model;

import java.util.ArrayList;

public class ModelKalimantan {
    public static String[][] data = new String[][]{
            {"Garantung",
                    "Di daerah Ponorogo Jawa Timur merupakan asal daerah Terompet Reog. Fungsi dari alat musik ini umumnya dipakai sebagai pengiring saat pertunjukan Reog Ponorogo. Alat musik tradisional tersebut dimainkan dengan cara di tiup karena termasuk dalam jenis alat musik tiup atau aerofon.",
                    "https://upload.wikimedia.org/wikipedia/id/5/5c/Garantung.gif"
            },
            {"Kintung",
                    "Kintung merupakan salah satu kesenian musik tradisional dari Suku Banjar, Kalimantan Selatan. Musik ini berasal dari daerah Kabupaten Banjar",
                    "http://wadaya.rey1024.com/api/uploads/7388438761535981841.jpg"
            },
            {"Kurinding",
                    "Kurinding adalah alat musik khas daerah Kalimantan Selatan. Guriding dimainkan oleh seniman dari etnis Bakumpai maupun juga Banjar. Guriding dibuat dari enau atau kayu mirip ulin yang hanya ada di daerah Muara teweh Barito utara.",
                    "data:image/jpeg;base64,"
            },
            {"Panting",
                    "Panting adalah alat musik yang berbentuk seperti gabus Arab tetapi lebih kecil dan memiliki senar. Panting dimainkan dengan cara dipetik.",
                    "http://wadaya.rey1024.com/api/uploads/12261221081535988211.jpg"
            },
            {"Sampek",
                    "Sampe/sampek adalah alat musik yang berfungsi untuk menyatakan perasaan, baik perasaan riang gembira, rasa sayang, kerinduan, bahkan rasa duka nestapa.",
                    "https://dayakculture.files.wordpress.com/2013/01/sampek.jpg"
            }
    };
    public static ArrayList<Model> getListData(){
        Model model = null;
        ArrayList<Model> list = new ArrayList<>();
        for (String[] aData : data) {
            model = new Model();
            model.setAlatMusik(aData[0]);
            model.setDetail(aData[1]);
            model.setGambar(aData[2]);
            list.add(model);
        }
        return list;
    }
}
